mapper.select:
SELECT *
FROM (SELECT ROWNUM R_NUM, p.*
FROM (SELECT *
FROM Post
ORDER BY P_ID DESC
) p
)
WHERE R_NUM BETWEEN #{begin} AND #{end}

SELECT FILE_NAME, ORI_NAME, P_ID
FROM PRODUCT_IMAGE
WHERE P_ID = #{p_id}

SELECT P_ID, M_ID, M_NAME, TITLE, CONTENT, PRICE, REGION_ID, REG_DATE
FROM POST
WHERE P_ID = #{p_id}

mapper.update:
UPDATE MEMBER
SET MEMBER_ID=#{newMemberId}, MEMBER_PW=#{newMemberPw}, MEMBER_NAME=#{newMemberName}, REGION_ID=#{newRegionId}
WHERE MEMBER_ID=#{searchMemberId}
